    public static int superDigit(String n, int k) {
        StringBuilder p = new StringBuilder();
        for (int i = 1; i <= k; i++) {
            p.append(n);
        }
        char[] characterP = p.toString().toCharArray();

        long sum = 0;
        for (char singleP : characterP) {
            int singleValue = Character.getNumericValue(singleP);
            sum += singleValue;
        }

        if (sum > 9) {
            sum = superDigit(Long.toString(sum), K);
        }

        return Math.toIntExact(sum);

    }
